            allItems.Add(new Items ( 10784, "Amulette du débutant", "Personne ne sait exactement ce que vous apporte cette amulette, mais tout le monde sait ce qu'elle vous retire : votre crédibilité." ));
            allItems.Add(new Items ( 10785, "Anneau du débutant", "Cet anneau protégera vos doigts des crottes de nez de vos adversaires." ));
            allItems.Add(new Items ( 10786, "Peau parfaite de Koalak Immature", "Le petit Koalak Immature à qui vous avez arraché cette peau, n'était pas d'accord pour vous la donner, mais qu'importe, vous étiez plus fort et plus cruel que lui." ));
            allItems.Add(new Items ( 10787, "Aile parfaite de Dragodinde Rousse Sauvage", "Cette Aile sent le soufre. Tout bon éleveur de Dragodindes vous dira que c'est la partie la plus tendre pour un barbeulcu. Assaisonnez tout de même la viande, car l'Aile est la partie la plus fade." ));
            allItems.Add(new Items ( 10788, "Aile parfaite de Dragodinde", "On peut dire que la Dragodinde domestique est bien meilleure que sa cousine sauvage. Déjà, elle n'a pas un arrière-goût de soufre, mais la chair est goûteuse à souhait, ce qui en fait le plat favori de tous les petits." ));
            allItems.Add(new Items ( 10789, "Poils irritants de Koalak Forestier", "Ces poils sont recouverts de sève de Kaliptus, ce qui leur confère un aspect luisant et brillant. Malheureusement, ils sont inutilisables en haute couture à cause de leur odeur de Dragodinde morte." ));
            allItems.Add(new Items ( 10790, "Os magique de Mama Koalak", "La Mama Koalak possède des os particulièrement résistants qui ne cèdent pas sous son poids. On ne peut pas en dire autant de Francisque Kabroule, le célèbre Ménestrel, qui perdit l'usage de ses deux bras en se faisant écraser par les énormes cuisses de la Mama Koalak. La curiosité est un vilain défaut." ));
            allItems.Add(new Items ( 10791, "Poils de Barbe urticants du Warko Marron", "Ces poils sont urticants, assez pour transformer les bises du Warko Marron en armes de destruction massives." ));
            allItems.Add(new Items ( 10792, "Potion de soin du débutant", "Cette fiole servait autrefois aux analyses d'urine. Elle contient désormais une potion capable de restaurer les points de vie perdus." ));
            allItems.Add(new Items ( 10793, "Pain du débutant", "Il est sec, il fait mal aux dents, il sent le slip de Bwork, mais il permet de regagner des points de vie. Arrêtez de faire la fine bouche." ));
            allItems.Add(new Items ( 10794, "Bottes du débutant", "Chaudes à l'intérieur et froides à l'extérieur, ces bottes vous permettront de botter les fesses des bonshommes de neige sans leur faire fondre le postérieur." ));
            allItems.Add(new Items ( 10795, "Poisson Deukloche du débutant", "Si vous frappez violemment ce poisson, vous l'entendrez résonner. Vous pouvez le manger et vous faire frapper pour vous transformer en instrument de musique." ));
            allItems.Add(new Items ( 10796, "Mexicanos du débutant", "Cette boulette de viande hachée épicée concentre tout ce qu'il y a de plus indigeste dans la contrée d'Amakna, mais c'est trop bon et votre petit corps en redemande." ));
            allItems.Add(new Items ( 10797, "Epée Tulante du débutant", "Sortie de son fourreau, cette épée peut couper le jambon, les Enutrofs, les unijambistes, les danseurs et les Enutrofs unijambistes enroulés dans une tranche de jambon qui dansent un soir de pleine lune. En revanche pour trancher les monstres, elle est plutôt inefficace." ));
            allItems.Add(new Items ( 10798, "Bouclier du débutant", "Ce bouclier ne vous protégera pas de votre bêtise." ));
            allItems.Add(new Items ( 10799, "Ceinture du débutant", "Vous n'arriviez pas à cacher votre épée ? Pas de problème, vous pourrez la coincer entre vos poignées d'amour et cette ceinture." ));
            allItems.Add(new Items ( 10800, "Cape du débutant", "Cette cape permet de voler, il suffit de fermer les yeux, serrer les poings, ouvrir la bouche et sauter d'une falaise en écartant les bras tout en criant " ));
            allItems.Add(new Items ( 10801, "Chapeau du débutant", "Ce chapeau cache admirablement votre calvitie. Malheureusement, il n'en n'existe pas d'assez grand pour cacher votre fausse modestie." ));
            allItems.Add(new Items ( 10802, "Dragoune Dorée", "Plus dorée que la Dragoune Noire, moins rose que la Dragoune Rose, et plus rare que la Dragoune Squelette, ce familier attirera tous les regards sur vous, même si vous êtes laid comme un Piou. Ce familier dévoreur d'âmes ne mange que les Dopeuls présents dans les temples de classe." ));
            allItems.Add(new Items ( 10804, "Cadeau Dragoune Dorée", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous." ));
            allItems.Add(new Items ( 10805, "Transformation Fantôme Dragoune Dorée", "Vous avez été transformé en fantôme afin de pouvoir pénétrer dans le sanctuaire des familiers." ));
            allItems.Add(new Items ( 10806, "Certificat de mise en chanil : Dragoune Dorée", "Ce certificat décrit les caractéristiques du familier que vous avez placé au Chanil." ));
            allItems.Add(new Items ( 10807, "Fantôme de Dragoune Dorée", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 10808, "Hormone de Dragoune Dorée", "Cette hormone de croissance déshydratée peut être utilisée dans la confection d'une puissante potion pour améliorer un familier." ));
            allItems.Add(new Items ( 10809, "Potion d\'Amélioration de Dragoune Dorée", "En utilisant cette puissante potion, vous augmenterez les capacités de votre familier de façon définitive. Attention, une seule dose sera prise en compte par votre familier, plusieurs tentatives provoqueront des résultats décevants." ));
            allItems.Add(new Items ( 10810, "Hormone de Chercheur d\'Ogrines", "Cette hormone de croissance déshydratée peut être utilisée dans la confection d'une puissante potion pour améliorer un familier." ));
            allItems.Add(new Items ( 10811, "Potion d\'Amélioration de Chercheur d\'Ogrines", "En utilisant cette puissante potion, vous augmenterez les capacités de votre familier de façon définitive. Attention, une seule dose sera prise en compte par votre familier, plusieurs tentatives provoqueront des résultats décevants." ));
            allItems.Add(new Items ( 10812, "Message anonyme", "" ));
            allItems.Add(new Items ( 10813, "Liste de noms", "Vous avez trouvé cette liste de noms dans la maison de Pandale Douce. Il semblerait qu'il s'agisse de Pandawas." ));
            allItems.Add(new Items ( 10814, "Caisse pour Pandetté", "Vous devez livrer cette caisse à Pandetté, au village d'Akwadala. Son contenu reste un mystère." ));
            allItems.Add(new Items ( 10815, "Liquide étrange", "Sel Dawa vous a demandé de quérir l'aide du Mage Ax pour analyser ce liquide." ));
            allItems.Add(new Items ( 10816, "Pétale Fané de Rose Démoniaque", "Ce pétale fripé a perdu de sa superbe et sa texture rappelle la peau des jeunes Enutrofs." ));
            allItems.Add(new Items ( 10817, "Anti-dépendant", "Ce prototype d'antidote devrait pouvoir soigner la dépendance au jus de ouassingue." ));
            allItems.Add(new Items ( 10818, "Hache de l\'initié", "Cette arme d'apparence rudimentaire vous donnera l'impression de savoir vous battre." ));
            allItems.Add(new Items ( 10825, "Goulifié", "Était-ce vraiment nécessaire de se donner tout ce mal pour en arriver là ?" ));
            allItems.Add(new Items ( 10826, "Avis d\'expulsion", "Comme vous vous êtes mal comporté avec vote hôte, ce dernier a décidé de vous fermer l'accès à sa crypte pour les prochaines 24 heures." ));
            allItems.Add(new Items ( 10827, "Potion de soin larvesque", "Un liquide orangé avec des bulles, personne ne peut vouloir boire ça." ));
            allItems.Add(new Items ( 10828, "Casque de boufbowl", "Il est dit que le ridicule ne tue pas. C'est exact, mais avec ce casque vous aurez vraiment l'air ridicule." ));
            allItems.Add(new Items ( 10829, "Kamapêche", "Grâce à ce bâton les négociations pécuniaires tourneront à votre avantage." ));
            allItems.Add(new Items ( 10830, "Banana Cape", "Ikko, un célèbre artiste d'Amakna fut le premier à oser porter cette cape. Tout le monde lui répétait pourtant : " ));
            allItems.Add(new Items ( 10831, "Bananagrume", "Flambée ou en tranche, nappée de chocolat fondu ou nature, ce fruit fait un succulent dessert." ));
            allItems.Add(new Items ( 10832, "Craqueloroche", "Cette pierre polie renferme un Craqueboule qui lui, ne l'est pas. Elle ne semble être utilisable qu'à proximité d'autres Craqueleurs ou Craqueboules..." ));
            allItems.Add(new Items ( 10833, "Chapain", "Ce pain est étonnant, quand on mord dedans, il miaule !" ));
            allItems.Add(new Items ( 10834, "Coiffe Wouaf", "Il faut peu de choses pour que vous ressembliez à un Ouginak. Attention aux puces." ));
            allItems.Add(new Items ( 10835, "Cachet du bizut", "Piétiné, mâchouillé, écrasé, lavé, repassé, séché, on vous a tout fait. Mais vous n'êtes plus un bleu); vous êtes recouvert de bleus." ));
            allItems.Add(new Items ( 10836, "Boufbamu", "Vous portez fièrement cette amulette, symbole de votre participation à cette grande manifestation sportive. Il faut ça pour rester fier après être passé sous la mêlée du B.C Trool." ));
            allItems.Add(new Items ( 10838, "Orangé", "Vous avez encore bu quelque chose sans savoir ce que c'était." ));
            allItems.Add(new Items ( 10839, "Monstre Pain", "On peut parfois se briser les dents sur du pain trop dur. Mangez celui-ci et vous recevrez un pain à vous faire tomber toutes les dents." ));
            allItems.Add(new Items ( 10840, "Monstre Pain consommable", "Celui-ci est plus tendre, bon appétit." ));
            allItems.Add(new Items ( 10841, "Marron", "Personne ne vous a prévenu des effets secondaires ?" ));
            allItems.Add(new Items ( 10842, "Transformation en chacha", "Voilà, il fallait bien que ça arrive ! Si vous recrachez des boules de poils, ne venez pas vous plaindre." ));
            allItems.Add(new Items ( 10843, "Note sur Krtek", "Vous avez noté à quelle heure Krtek s'est pris une raclée. Ainsi, vous pouvez savoir quand retourner lui en mettre une nouvelle." ));
            allItems.Add(new Items ( 10844, "Mmmmm", "Mmmmm mm mmmmmm mmmmm, mmm mmm mm mmmmmm mmmmm mmmmmmm mmmmmm mmm mmmm'm mmmmm mmmm mmmmmmm." ));
            allItems.Add(new Items ( 10846, "Phéro-Cape", "Pris d'une étrange frénésie, les tofus que vous croisez veulent se frotter contre votre jambe. Ce n'est tout de même pas cette cape qui les attire ?" ));
            allItems.Add(new Items ( 10847, "Bottes d\'Hulkrap", "Hulkrap enfilait ces bottes une fois par an, mais il les enlevait tous les jours. On peut donc en déduire que ces bottes sont magiques." ));
            allItems.Add(new Items ( 10848, "Bottes de Klüme", "Ces bottes coquées peuvent servir d'enclumes. Son ancien propriétaire vous conseille vivement de ne pas danser avec." ));
            allItems.Add(new Items ( 10849, "Ceinture d\'Hulkrap", "Cette ceinture est fabriquée autour d'une armature métallique aimantée qui attire les bourses des imprudents qui oseraient se frotter à vous." ));
            allItems.Add(new Items ( 10850, "Ceinture de Klüme", "Klüme avait trois faiblesses, les araknes, les chatouilles et les araknes chatouilleuses." ));
            allItems.Add(new Items ( 10851, "Ceinture de Kloug", "Cette ceinture est encore recouverte de farine. Peut-être parce qu'elle servait de tablier à Kloug le Boulanger Sombre. A moins que ce ne soit la farine qu'il utilisait pour se poudrer le postérieur." ));
            allItems.Add(new Items ( 10852, "Amulette d\'Hulkrap", "Hulkrap a jadis volé cette amulette à Gonztipay, un gobelin dénué de toute forme d'humour, même le plus lourd." ));
            allItems.Add(new Items ( 10853, "Amulette de Kloug", "Cette amulette vous permet de pétrir la peau de votre cou sans utiliser vos mains." ));
            allItems.Add(new Items ( 10854, "Anneau de Klüme", "Gontran Klüme le forgeron a échangé cet anneau contre une épée qui appartenait à la Dame Antium." ));
            allItems.Add(new Items ( 10855, "Anneau de Kloug", "Cet anneau a été trempé dans une multitude de levures différentes. On raconte qu'il est capable de transformer en moisissure ceux qui le portent. Certains aventuriers ne perçoivent aucune différence après l'avoir porté." ));
            allItems.Add(new Items ( 10856, "Hache d\'Hulkrap", "Hulkrap était un très mauvais bandit rongé par les remords, il se sentait obligé de rendre ce qu'il avait volé. Cette arme magique est encore imprégnée de l'âme du Bandit Hulkrap. En l'équipant, vous pourrez peut-être parvenir à l'incarner." ));
            allItems.Add(new Items ( 10857, "Marteau de Klüme", "Ce marteau a permis de forger une braguette en Kriptonite pour le Captain Chafer. Cette arme magique est encore imprégnée de l'âme du Forgeron Sombre Klüme. En l'équipant, vous pourrez peut-être parvenir à l'incarner." ));
            allItems.Add(new Items ( 10858, "Baguette de Kloug", "Kloug aimait se montrer généreux en distribuant des pains à ceux qui osaient lui parler. Il utilisait cette baguette pour rendre son jugement et punir les innocents. Cette arme est encore imprégnée de la magie de Kloug le Boulanger Sombre. Vous pourrez peut-être réussir à l'incarner en utilisant cette arme." ));
            allItems.Add(new Items ( 10860, "Potion de changement de nom", "Cette potion permet de changer de nom. Plus personne ne se souviendra que vous vous appeliez Xx-Roxxor-naruto-killer-féca-xX. Une nouvelle vie sans moquerie s'offre à vous. Vous allez enfin devenir quelqu'un." ));
            allItems.Add(new Items ( 10861, "Potion de changement des couleurs", "Du rose pour les cheveux, du mauve pour les chaussettes, et du fuchsia pour la peau. La nature ne vous a peut-être pas gâté, mais vous avez enfin l'opportunité d'y remédier. L'occasion rêvée pour voir la vie en caca d'oie." ));
            allItems.Add(new Items ( 10862, "Potion de changement de sexe", "Cette potion à boire directement au goulot vous permettra de devenir lui si vous étiez elle ou de devenir elle si vous étiez lui. A consommer sans modération." ));
            allItems.Add(new Items ( 10863, "Raknaille", "Cette Arakne est vraiment très mal élevée, elle saute partout, elle crie, elle mord n'importe qui, mais vous l'aimez beaucoup sans savoir pourquoi. La nature l'a en outre dotée d'un appétit féroce, ce familier dévoreur d'âme adore attaquer puis dévorer les Moskitos et les Larves." ));
            allItems.Add(new Items ( 10864, "Meuch", "Ce Champ Champ pas comme les autres a été élevé par une meute de Mulous. Ça n'explique pas pourquoi il a du poil sous les aisselles. Ce familier dévoreur d'âmes adore les fruits de mer et ne mange que les créatures des plages." ));
            allItems.Add(new Items ( 10865, "Tofoudre", "Ce Tofu a reçu la foudre 11 fois durant sa croissance. Vous recevrez des décharges électriques si vous le caressez et il vous foudroiera si vous ne le caressez pas. Pour une fois vous avez le choix. Ce dévoreur d'âme adore les rats et s'en nourrit dès que l'occasion se présente à lui." ));
            allItems.Add(new Items ( 10866, "Pykur", "Ce familier aime les cœurs d'artichauts au sang de Rasboul, les endives aux poils de Bitouf, et les chansons de Francisque Kabroule. Il semble également apprécier votre compagnie. Ce dévoreur d'âme ne mange que les créatures de la Jungle Obscure." ));
            allItems.Add(new Items ( 10867, "Fantôme de Raknaille", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
