            allItems.Add(new Items ( 10899, "Bâton du Manitou Zoth", "Le Manitou Zoth était un ancien manutentionnaire, il déplaçait les problèmes au lieu de les résoudre. Mais qu'importe, il était craint au sein de la communauté des Zoths et criait plus fort que tous les autres, ce qui suffit pour être chef chez les Zoths. Ce bâton est encore animé par l'âme du Manitou Zoth. Vous pourrez peut-être réussir à l'incarner en utilisant cette arme." ));
            allItems.Add(new Items ( 10900, "Eclat de coquille", "Vous avez en main tout ce qui reste de ces gigantesques coquilles aperçues sur la presqu'île des dragoeufs. Ca ne peut même pas faire un bon cendrier." ));
            allItems.Add(new Items ( 10901, "Gigantesque Canine", "Grasmera et Grozilla se sont affrontés lors d'un épique combat. Tout ce qu'il reste est cette canine de taille démesurée. Qui croira que vous y étiez ?" ));
            allItems.Add(new Items ( 10902, "Slip du Manitou Zoth", "A présent, vous pouvez dire sans mentir que le Manitou Zoth est tout nu sous son pagne." ));
            allItems.Add(new Items ( 10903, "Clef de l\'antre du Manitou Zoth", "Cette clef permet d'aller combattre le Manitou Zoth." ));
            allItems.Add(new Items ( 10904, "Grozillamu", "Cette amulette témoigne de votre incontestable capacité à casser les œufs sans faire d'omelettes." ));
            allItems.Add(new Items ( 10906, "Bouclier Draconique", "Façonné à partir d'une écaille de Grozilla, ce lourd bouclier est d'une prestance exceptionnelle. Seuls de rares et redoutables combattants arborent fièrement ce pavois." ));
            allItems.Add(new Items ( 10908, "Cadeau Donjons Beta-Test DOFUS 2.0", "Ce cadeau vous a été offert car vous avez passé plus de temps dans les donjons du beta-test DOFUS 2.0 que le commun des mortels." ));
            allItems.Add(new Items ( 10909, "Phéromone de Tofu", "Cette poudre de phéromone séchée pourrait attirer un Tofu à des kilomètres si vos propres phéromones ne les asphyxiaient pas." ));
            allItems.Add(new Items ( 10910, "Cadeau Coffret Collector DOFUS 2.0", "Cette panoplie fera de vous une lumière, à condition de vous taire. Dans le cas contraire, elle pourra toujours impressionner les habitants d'Astrub." ));
            allItems.Add(new Items ( 10911, "Potion de Rappel de M. Scouty", "Deux fois plus crémeuse et deux fois plus onctueuse qu'une potion de rappel classique, la « Potion de Rappel de M. Scouty » vous permettra de vous téléporter au zaap d'Astrub depuis n'importe quel endroit. Le double effet de cette potion vous assure un usage prolongé, qui dure longtemps, vraiment longtemps. nLa « Potion de Rappel de M. Scouty » est une marque déposée des entreprises Keurk & Spouky." ));
            allItems.Add(new Items ( 10912, "Cadeau de Nowel ridicule", "Tous les cadeaux de Nowel n'ont pas la même taille. Les aventuriers qui n'ont pas été très courageux  gagnent des cadeaux ridicules ainsi que le droit de pleurer et d'être moqués." ));
            allItems.Add(new Items ( 10913, "Cadeau de Nowel satisfaisant", "Celui qui a fait ce cadeau est bien prétentieux, il était persuadé que son contenu serait satisfaisant." ));
            allItems.Add(new Items ( 10914, "Cadeau de Nowel potentiellement surpuissant", "Ce cadeau a du potentiel si l'on fait confiance à ce qui est écrit sur le nœud en tissu. Cependant, tous les aventuriers expérimentés vous le diront : ne faîtes jamais confiance à ce qui est écrit sur les nœuds en tissu." ));
            allItems.Add(new Items ( 10915, "Ordre de mission de Félix Nowel", "Félix Nowel vous a révélé l'identité d'un des cambrioleurs de l'entrepôt du Père Nowel. Si vous ne parvenez pas à le vaincre d'ici deux heures, vous pouvez, si vous le souhaitez,  retourner auprès de Félix Nowel pour obtenir un nouvel ordre de mission." ));
            allItems.Add(new Items ( 10916, "Félicitations de Félix Nowel", "Votre victoire a été très appréciée par Félix Nowel. Revenez le voir dans 24 heures, il aura encore besoin de vos services si les caisses n'ont pas toutes été récupérées d'ici là." ));
            allItems.Add(new Items ( 10917, "Petite caisse de cadeaux", "Cette petite caisse provient de l'entrepôt du Père Nowel. La restituer à son propriétaire fera de vous un héros." ));
            allItems.Add(new Items ( 10918, "Caisse de cadeaux", "Cette caisse provient de l'entrepôt du Père Nowel. La restituer à son propriétaire fera de vous un héros." ));
            allItems.Add(new Items ( 10919, "Grosse caisse de cadeaux", "Cette grosse caisse provient de l'entrepôt du Père Nowel. La restituer à son propriétaire fera de vous un héros." ));
            allItems.Add(new Items ( 10920, "Biscuits de Nowel", "Voici de délicieux biscuits cuisinés avec amour. Dommage que vous ne puissiez pas y goûter." ));
            allItems.Add(new Items ( 10921, "Cadeau scellé du Minotoboule", "Nul ne sait ce que ce cadeau contient, le Minotoboule ayant emporté ce secret dans sa tombe. Est-il nécessaire de rappeler que c'est entièrement votre faute ? Vraiment, il y a de quoi avoir les boules." ));
