            allItems.Add(new Items ( 10955, "Anneau du Corbeau Noir", "Quoi de plus normal pour un volatile que d'être bagué !" ));
            allItems.Add(new Items ( 10956, "Bottes du Corbeau Noir", "Cette paire de bottes, comme tout le reste de la panoplie du Corbeau Noir, est passée de pied en pied à travers les âges. Ce qui explique sa forte odeur de Clakoss..." ));
            allItems.Add(new Items ( 10957, "Cape du Corbeau Noir", "Du fil, une aiguille, quelques plumes prélevées sur des dépouilles de Corbacs en décomposition, et vous voilà en possession de la plus chic des capes !" ));
            allItems.Add(new Items ( 10958, "Casque du Corbeau Noir", "Inutile de vous cacher ! On vous a démasqué, c'est donc vous, le Corbeau Noir !" ));
            allItems.Add(new Items ( 10959, "Pik Epik du Corbeau Noir", "Vous y avez laissé des plumes, mais la voilà enfin entre vos mains ! L'épée du Corbeau Noir..." ));
            allItems.Add(new Items ( 10960, "Cadeau Anneau du Corbeau Noir", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous. nAttention, ce cadeau ne peut-être ouvert que sur DOFUS 2.0." ));
            allItems.Add(new Items ( 10961, "Cadeau Pik Epik du Corbeau Noir", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous. nAttention, ce cadeau ne peut-être ouvert que sur DOFUS 2.0." ));
            allItems.Add(new Items ( 10966, "Transformation en larve", "Les mauvaises langues insinueraient que cela révèle votre nature profonde. Ont-elles vraiment tort ?" ));
            allItems.Add(new Items ( 10967, "Cervelle de larve", "Il faut vraiment être désespéré pour manger ça. Non, ne faites pas ça !" ));
            allItems.Add(new Items ( 10968, "Transformation en wabbit", "Étonnamment, vous n'avez pas envie de cawottes." ));
            allItems.Add(new Items ( 10969, "Potion de Fwaîchew", "La douceur d'une cawotte, la fraîcheur d'une dragodinde marathonienne. Rien de tel pour vous remettre d'aplomb. Santé !" ));
            allItems.Add(new Items ( 10970, "Sueur de dragodinde", "Mais comment avez vous fait pour récupérer ça ? Et pourquoi ? Vous êtes vraiment bizarre." ));
            allItems.Add(new Items ( 10971, "Sac ripant", "Équipé de ce sac, vous pourrez faire les quatre cents coups. Et toujours avec le sourire, petit galopin va !" ));
            allItems.Add(new Items ( 10972, "Corbac suiveur", "Bien que très affectueux, vous soupçonnez ce volatile de fuir à tire-d'ailes au premier signe de danger." ));
            allItems.Add(new Items ( 10973, "Potion de destination inconnue", "Votre instinct vous dicte de ne pas avaler le contenu de ce bocal. En fait, c'est surtout votre nez : cette mixture empeste comme les aisselles d'un Bwork centenaire." ));
            allItems.Add(new Items ( 10974, "Fée d\'artifice festive", "C'est la fêêêêteuuuh, la fêêêêteuhh !" ));
            allItems.Add(new Items ( 10985, "Chacha des Glaces", "Ce Chacha a connu l'hiver du grand nord, même s'il n'a jamais vu le grand nord. Personne ne sait comment il a pu arriver en Amakna, les anciens prétendent qu'il aurait été importé de l'île de Frigost pour lutter contre la prolifération des Wabbits. Ce familier dévoreur d'âmes ne mange que les Wabbits." ));
            allItems.Add(new Items ( 10986, "Fantôme de Chacha des Glaces", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 10991, "Cadeau Chacha des Glaces", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous. nAttention, ce cadeau ne peut-être ouvert que sur DOFUS 2.0." ));
            allItems.Add(new Items ( 11008, "Certificat de mise en Chanil : Chacha des Glaces", "Ce certificat décrit les caractéristiques du familier que vous avez placé au Chanil." ));
            allItems.Add(new Items ( 11009, "Hormone de Chacha des Glaces", "Cette hormone de croissance déshydratée peut être utilisée dans la confection d'une puissante potion pour améliorer un familier." ));
            allItems.Add(new Items ( 11010, "Potion d\'amélioration de Chacha des Glaces", "En utilisant cette puissante potion, vous augmenterez les capacités de votre familier de façon définitive. Attention, une seule dose sera prise en compte par votre familier, plusieurs tentatives provoqueront des résultats décevants." ));
            allItems.Add(new Items ( 11011, "Tifoux Tigré", "Plus fou que le Tifoux commun mais surtout beaucoup moins roux, ce familier peut être trempé dans l'eau sans qu'il ne soit nécessaire de se boucher le nez. Ce familier se nourrit des créatures qui vivent dans l'Arbre Hakam." ));
            allItems.Add(new Items ( 11016, "Fantôme de Tifoux Tigré", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 11026, "Certificat de mise en Chanil : Tifoux Tigré", "Ce certificat décrit les caractéristiques du familier que vous avez placé au Chanil." ));
            allItems.Add(new Items ( 11031, "Hormone de Tifoux Tigré", "Cette hormone de croissance déshydratée peut être utilisée dans la confection d'une puissante potion pour améliorer un familier." ));
            allItems.Add(new Items ( 11036, "Potion d\'Amélioration de Tifoux Tigré", "En utilisant cette puissante potion, vous augmenterez les capacités de votre familier de façon définitive. Attention, une seule dose sera prise en compte par votre familier, plusieurs tentatives provoqueront des résultats décevants." ));
            allItems.Add(new Items ( 11049, "Coiffe de Grilliane", "Grilliane était un très grand magicien, célèbre pour avoir réinventé la poudre d'escampette, après avoir oublié comment la fabriquer." ));
            allItems.Add(new Items ( 11050, "Cape de Grilliane", "Cette cape est censée protéger votre postérieur des morsures de monstroplantes." ));
            allItems.Add(new Items ( 11051, "Amulette de Grilliane", "Grilliane portait cette amulette autour du coup lorsqu'il... Lorsqu'il... Quelle importance ? Il est mort et désormais cette amulette vous appartient." ));
            allItems.Add(new Items ( 11052, "Bottes de Grilliane", "Grilliane le magicien a échangé un anneau magique contre cette paire de bottes. Il semblerait qu'il ait perdu au change puisqu'elles ne sont même pas rembourrées." ));
            allItems.Add(new Items ( 11055, "Chapeau de Gadjète", "Gadjète était l'un des bricoleurs les plus créatifs du Monde des 12. Malheureusement il était également l'un des plus étourdis. On raconte qu'il a confondu son enclume et la tête de sa femme plus d'une fois et qu'elle ne s'en plaignait pas." ));
            allItems.Add(new Items ( 11056, "Cape de Gadjète", "Gadjète a fabriqué cette cape en espérant qu'elle puisse lui permettre de voler et ainsi échapper plus facilement aux punitions administrées quotidiennement par sa femme." ));
            allItems.Add(new Items ( 11057, "Ceinture de Gadjète", "En mettant cette ceinture à l'envers et en la serrant suffisamment fort, vous pourrez peut-être activer le module intégré de rétromagie. Il n'a jamais fonctionné, mais vous pourrez l'activer tout de même. Vous pourrez donc la revendre à une personne aussi naïve que vous." ));
            allItems.Add(new Items ( 11058, "Anneau de Gadjète", "Porté par plusieurs générations de Bricoleurs, cet anneau protège une phalange de vos coups de marteau. Malheureusement, il n'est jamais vendu par lot de cinq." ));
            allItems.Add(new Items ( 11063, "Cadeau Coiffe de Grilliane", "En secouant vigoureusement ce cadeau, vous comprenez qu'il n'est pas vide. Peut-être que pour une fois, les dieux ne se sont pas moqués de vous. nAttention, ce cadeau ne peut-être ouvert que sur DOFUS 2.0." ));
