            allItems.Add(new Items ( 11465, "Kometh", "Amoureux inconditionnel de l'humour douteux des glutins, Kometh a été subjugué par le vôtre. Il a donc décidé de quitter les forêts enneigées de l'île de Nowel pour vivre la plus formidable des aventures à vos côtés. Son secret pour accélérer la repousse de ses deux magnifiques appendices, une fois la période de mue passée ? Faire feu de tout bois... Jusqu'à se nourrir de ce dernier ! Avec lui : rien ne se perd, tout se transforme !" ));
            allItems.Add(new Items ( 11466, "Fantôme de Kometh", "Ce Fantôme n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
