            allItems.Add(new Items ( 11469, "Certificat de mise en chanil : Kometh", "Ce certificat décrit les caractéristiques du familier que vous avez placé au Chanil." ));
            allItems.Add(new Items ( 11470, "Hormone de Kometh", "Cette hormone de croissance déshydratée peut être utilisée dans la confection d'une puissante potion pour améliorer un familier." ));
            allItems.Add(new Items ( 11471, "Tioube", "Très spéciale, cette cape comptabilise le nombre de personnes qui se sont intéressées à elle. Elle est parfois utilisée dans le but d'exposer ses trophées de guerre, en les y attachant, et ainsi jauger l'intérêt qu'ils peuvent susciter. Certains aventuriers et commerçants vont même parfois jusqu'à former une chaîne de Tioubes lorsqu'ils veulent promouvoir plusieurs objets à la fois." ));
            allItems.Add(new Items ( 11472, "Bouclier Hitche", "En portant ce bouclier, vous serez visible de loin. Ceci permettra à n'importe qui de suivre vos aventures les plus palpitantes, où que vous soyez, y compris et surtout à la taverne d'Astrub." ));
            allItems.Add(new Items ( 11473, "Caskwitteur", "Ce casque peut parler : il répète vos paroles et les transmet aux autres Caskwitteurs à proximité qui les répéteront à leur tour. Il fut très utile en complément des Pious voyageurs jusqu'à ce qu'il se popularise davantage : les messages transmis ont tendance à revenir au Caskwitteur d'origine, générant une boucle et donc un brouhaha incessant. Aujourd'hui il est utilisé dans le jeu du Ragekwitt visant à tester la patience des participants : chacun en pose un sur sa tête et celui qui supporte le bruit le plus longtemps est vainqueur." ));
            allItems.Add(new Items ( 11474, "Clef du Belvédère d\'Ilyzaelle", "Cette clef permet de rentrer dans le belvédère d'Ilyzaelle. Attention, elle sera détruite après utilisation." ));
            allItems.Add(new Items ( 11475, "Clef du Sanctuaire de Qu\'Tan", "Cette clef permet d'ouvrir la grille permetant d'entrer dans le sanctuaire de Qu'Tan. Attention, elle sera détruite après utilisation." ));
            allItems.Add(new Items ( 11476, "Bandeau Komintot Polykrome", "Cette coiffe aux allures familières a été enchantée par Hel Munster lui-même suite aux demandes incessantes d'un célèbre couturier d'Astrub. Il désirait proposer une collection qui s'adapte aux couleurs de l'individu qui la porte. Décolorée par un procédé top secret, vous reconnaissez pourtant l'odeur caractéristique de la bave de Mulou qui s'en dégage... Beurk !" ));
