            allItems.Add(new Items ( 11526, "Plume nacrée d\'Ilyzaelle", "Cette plume brille d'une lueur qui dépasse l'entendement. Ilyzaelle s'en sert pour voyager très rapidement à travers le grand continent. Bien entendu, ça va être compliqué désormais." ));
            allItems.Add(new Items ( 11527, "Cheveu d\'Ilyzaelle", "Ce cheveu dispose de propriétés qui rendrait fou n'importe quel tailleur : il est incroyablement fin et difficile à manipuler. Il est cependant très soyeux et souple, idéal pour une personne de votre trempe. Une chose est sûre : il sent bon." ));
            allItems.Add(new Items ( 11528, "Arc brisé du Tireur d\'Élite", "Il ne reste plus grand chose de ce qu'était ce bel arc, on distingue tout de même un magnifique ornement recouvrant le manche. Il y a également l'insigne de la garde royale de Bonta fermement incrusté dans le bois." ));
            allItems.Add(new Items ( 11529, "Pierre Perchée du Craqueleur Céleste", "Les légendes racontent que seuls les plus téméraires peuvent trouver cette pierre sur la plus haute cime de la plus haute montagne de Cania. Elle attendrait le baiser d'un preux chevalier, ce qui la changerait en princesse... La réalité est parfois bien moins romantique : vous allez plutôt vous prendre un mur." ));
            allItems.Add(new Items ( 11530, "Résidu de sort temporel", "Les puissants mages de l'école de Bonta mettent ces sorts en bouteille afin de pouvoir les utiliser rapidement en combat. Il faut donc être prudent en les manipulant au risque de créer une faille dans l'espace temps d'une ampleur formidable. Tout le contraire de ce que vous pouvez faire donc." ));
            allItems.Add(new Items ( 11531, "Corne en Platine de Palidane", "A force de polir cette corne à longueur de journée, Palidane ne voit plus rien quand le soleil reflète dessus." ));
            allItems.Add(new Items ( 11532, "Défense Maléfique", "Cette corne représente à elle seule tout ce qui caractérise l'ex-lieutenant d'Uk'Not'Allag : impressionnante, malodorante et très puissante." ));
            allItems.Add(new Items ( 11533, "Ongle Désincarné de Qu\'Tan", "Avec une douleur pareille, pas surprenant que Qu'Tan soit aussi ronchon. D'autant plus que Bworknroll lui a conseillé de le mettre dans de l'eau tiède salée. Forcément, l'eau étant une denrée rare à Sidimote, il dut se rabattre sur de la lave. On en aurait presque de la peine." ));
            allItems.Add(new Items ( 11534, "Canine de Démon", "Aiguisée comme une lame de rasoir, cette dent peut arracher la chair d'un aventurier trop imprudent en moins de temps qu'il soit possible de dire « aïe ! »." ));
            allItems.Add(new Items ( 11535, "Peau Malodorante de Démon", "Cette peau à la texture écailleuse et froide au toucher apporte une protection supérieure si elle est portée. Une fois transformée en pièce d'équipement, elle permettra également de repousser la gente féminine, ce qui, selon certains connaisseurs un peu vieux, justifie la contrainte de l'odeur." ));
            allItems.Add(new Items ( 11536, "Os de Chafer Maudit", "A l'instar de son cousin avec lequel il entretient des relations très cordiales (mais en toute amitié), le Chafer Maudit a les os solides. C'est probablement dû a une alimentation pas trop grasse, trop sucrée ou trop salée." ));
            allItems.Add(new Items ( 11537, "Morceau de Bâton du Sorcier Démon", "Contrairement à ce que l'on pourrait penser, les Sorciers Démons invoqués par Qu'Tan n'utilisent pas un catalyseur ordinaire. Il s'agit d'une sorte de lance imprégnée de la magie noire de son maître. Décidément, quelle subtilité..." ));
            allItems.Add(new Items ( 11538, "Flèche Ensorcelée", "Cette flèche est, par définition, spéciale. Elle le serait encore plus si vous n'étiez pas déjà criblé d'une dizaine d'autres. Au moins, celle-ci est en bon état." ));
            allItems.Add(new Items ( 11539, "L\'Arc Adie", "Cet arc a été assemblé avec des matériaux d'une grande qualité. En le saisissant, un grand pouvoir s'en dégage et cela se ressent également dans les flèches tirées. " ));
            allItems.Add(new Items ( 11540, "Potion de protection enténébrée", "Cette potion vous permet de ne pas être affecté par l'écrasante puissance d'Ilyzaelle. Un objet indispensable, donc." ));
            allItems.Add(new Items ( 11541, "Potion de protection opalescente", "Cette potion vous permet de ne pas être affecté par l'écrasante perfidie de Qu'Tan. Un objet indispensable, donc." ));
            allItems.Add(new Items ( 11542, "Cape Ilyza\'aile", "En revêtant cet accoutrement, attendez-vous à une augmentation non négligeable des traquenards, des embuscades et d'autres attaques en tout genre des assaillants de la cité noire. Si ça se trouve, au fond, vous cherchez peut-être volontairement toute cette attention." ));
            allItems.Add(new Items ( 11543, "Ilyzanneau", "On vous a vendu cet anneau comme un objet unique en son genre, qui procurerait des pouvoirs convoités par des chevaucheurs de Dragodindes tout de noir vêtus. Il suffit d'observer les autres aventuriers à Bonta pendant l'heure de pointe pour constater que ce n'est pas le cas." ));
            allItems.Add(new Items ( 11544, "Qu\'Tanneau", "A première vue, cet anneau a l'air extrêment inconfortable voire même dangereux pour vos petits doigts. A bien y regarder de plus près : c'est le cas." ));
            allItems.Add(new Items ( 11545, "Ceinture à plumes", "Cette ceinture vous permet, certes, de dissimuler efficacement votre postérieur); mais peut également être portée comme un couvre-chef pour vos soirées mondaines." ));
            allItems.Add(new Items ( 11546, "Amulette de Qu\'Tan", "Serti d'un oeil de démon, cette amulette a la forme originale vous embrochera dès que vous vous mettrez à courir. Une sorte de vengence de la part du démon qui vous rappellera sans cesse que toute action engendre une réaction." ));
            allItems.Add(new Items ( 11547, "Bottes Qu\'Tanées", "Ces bottes sentent le souffre et le fromage. En plus de vous procurer de la puissance, elles dissimuleront vos odeurs de pieds, c'est tout bénef' !" ));
            allItems.Add(new Items ( 11548, "Couvre-chef corné de Qu\'Tan", "Qu'Tan se servait de ces cornes afin de punir ses sujets quand ils revenaient bredouille de la chasse, ou plutôt " ));
            allItems.Add(new Items ( 11549, "Bourse de 30000 Kamas", "Cette bourse est très lourde, elle contient sûrement une vraie fortune." ));
            allItems.Add(new Items ( 11550, "Potion de protection enténébrée", "Cette potion vous permet de ne pas être affecté par l'écrasante puissance d'Ilyzaelle. Un objet indispensable, donc." ));
            allItems.Add(new Items ( 11551, "Potion de protection opalescente", "Cette potion vous permet de ne pas être affecté par l'écrasante perfidie de Qu'Tan. Un objet indispensable, donc." ));
            allItems.Add(new Items ( 11552, "Fatigue", "La salle du boss est enveloppée d'une puissante aura qui vous affaiblit." ));
            allItems.Add(new Items ( 11553, "Parchemin Doré", "(Double clic pour gagner de l'expérience) nCe parchemin permet de gagner de l'expérience sans prendre de risques." ));
            allItems.Add(new Items ( 11554, "Dragoune en Armure", "Inspirée par la légendaire Dragodinde en armure, ce spécimen rare de Dragoune en armure veut lui aussi entrer dans la légende... et peut-être même, voler la vedette à son aînée !" ));
            allItems.Add(new Items ( 11555, "Casque Dragarmure", "Impossible de passer inaperçu avec ce casque sur la tête : les vieux de la vieille, les vrais de vrais, reconnaîtront aussitôt votre valeur, votre goût incontestable et surtout l'apparence de la célèbre Dragodinde en armure." ));
            allItems.Add(new Items ( 11556, "Bouclier Dragarmure", "Forgé à partir de la même cuirasse que celle arborée par la célèbre Dragodinde en armure, ce bouclier freinera considérablement les attaques de votre adversaire, mais jamais votre ardeur sauvage." ));
            allItems.Add(new Items ( 11557, "Cape Dragarmure", "Elle flotte au vent, avec la même magnificence que la Dragodinde en armure qui l'a inspirée. La cape Dragarmure vous donnera l'impression de voler. Veillez toutefois à redescendre sur terre au moment de combattre..." ));
            allItems.Add(new Items ( 11558, "Fantôme de Dragoune en Armure", "Ce Fantôme de Dragoune en Armure n'a pas encore quitté le monde des vivants, il ne tient qu'à vous de trouver une façon de le ressusciter, ou de l'abandonner lâchement une bonne fois pour toutes." ));
            allItems.Add(new Items ( 11559, "Certificat de Mise en Chanil : Dragoune en Armure", "Ce certificat décrit les caractéristiques du Dragoune en Armure que vous avez placé au chanil." ));
            allItems.Add(new Items ( 11567, "Gemme Spirituelle de Qu\'Tan", "Gagnée en récompense de vos victoires en duel, cette gemme referme l'âme d'un monstre. A conserver à l'abri de la lumière dans un endroit sec. Secouer avant usage." ));
            allItems.Add(new Items ( 11568, "Gemme Spirituelle d\'Ilyzaelle", "Gagnée en récompense de vos victoires en duel, cette gemme referme l'âme d'un monstre. A conserver à l'abri de la lumière dans un endroit sec. Secouer avant usage." ));
            allItems.Add(new Items ( 11569, "Viande d\'Ange", "Un bon morceau de viande coupé dans la partie la plus tendre d'un Ange. On ne veut pas savoir qu'elle est cette fameuse partie." ));
            allItems.Add(new Items ( 11570, "Viande d\'Ange**", "Un bon morceau de viande coupé dans la partie la plus tendre d'un Ange. On ne veut pas savoir qu'elle est cette fameuse partie." ));
