        public Primaire() {
            InitializeComponent();

            new Thread(() => {
                waitForm = new load();
                new Thread(() => waitForm.ShowDialog()).Start();
            }).Start();


            Visible = false;
            Opacity = 0;
            ShowInTaskbar = false;

            conn = new SQLiteConnection("Data Source=CommunityTools.db; Version = 3; New = True; Compress = True; ");
            try {
                conn.Open();
            } catch (Exception ex) {
                MessageBox.Show(ex.Message);
            }

            ConfigTable();
            ConfigItems();
            ConfigCrafts();
            ConfigDragodindes();
            ConfigServeurs();
            ConfigType();


            lv_banque = listView1;
            lv_craft = listView2;
            lv_inventaire = listView3;
            lv_hdv = listView4;
            lv_items = listView5;
            lv_rentpnj = listView6;
            lv_rentvente = listView7;

            rtb_r_action = rtb_rec_actions;
            rtb_r_brute = rtb_rec_brute;
            rtb_e_action = rtb_env_actions;
            rtb_e_brute = rtb_env_brute;


            LoadItems();

            ConfigSniff _config = new ConfigSniff(waitForm);

            traite = new TraitementPackets();
            DialogResult result = _config.ShowDialog();
            if (result == DialogResult.OK) {
                device = _config.allDevices[_config.choixDevices];

                sniffer = new Sniffer(device);
                Visible = true;
                Opacity = 100;
                ShowInTaskbar = true;
            }


            _thBoucle = new Thread(sniffer.Lecture);
            _thBoucle.Start();
        }

        void ConfigTable() {
            waitForm.loadUpdate("Étape 1/6 : Création de la base de donnée");
            SQLiteCommand _cmd = conn.CreateCommand();
            _cmd.CommandText = "CREATE TABLE IF NOT EXISTS \"Items\" ( \"id\" INT NOT NULL, \"lvl\" TINYTEXT NOT NULL, \"nom\" MEDIUMTEXT NOT NULL, \"desc\" LONGTEXT NOT NULL, \"prix1\" INT NULL, \"prix10\" INT NULL, \"prix100\" INT NULL, \"prixPnj\" INT NULL, \"qty\" INT NULL, \"cout\" INT NULL, PRIMARY KEY (\"id\"));"
                + "CREATE TABLE IF NOT EXISTS \"Crafts\" ( \"id\" INT NOT NULL, \"ress1\" VARCHAR(15) NOT NULL, \"ress2\" VARCHAR(15) NOT NULL,\"ress3\" VARCHAR(15) NOT NULL,\"ress4\" VARCHAR(15) NOT NULL,\"ress5\" VARCHAR(15) NOT NULL,\"ress6\" VARCHAR(15) NOT NULL,\"ress7\" VARCHAR(15) NOT NULL,\"ress8\" VARCHAR(15) NOT NULL,PRIMARY KEY (\"id\"));"
                + "CREATE TABLE IF NOT EXISTS \"Dragodindes\" (\"id\" INTEGER NULL DEFAULT 0, \"nom\"	MEDIUMTEXT NOT NULL, PRIMARY KEY(\"id\"));"
                + "CREATE TABLE IF NOT EXISTS \"Servers\" (\"id\" INTEGER NULL DEFAULT 0, \"nom\" MEDIUMTEXT NOT NULL, \"version\" VARCHAR(5) NOT NULL, \"ip\" VARCHAR(60) NOT NULL, PRIMARY KEY(\"id\"));"
                + "CREATE TABLE IF NOT EXISTS \"Banque\" ( \"id\" INT NOT NULL, \"qty\" INT NOT NULL, PRIMARY KEY (\"id\"));"
                + "CREATE TABLE IF NOT EXISTS \"Inventaire\" (\"id\" INT NOT NULL, \"qty\" INT NOT NULL, PRIMARY KEY (\"id\"));"
                + "CREATE TABLE IF NOT EXISTS \"Type\" (\"id\" INT NOT NULL, \"type\" MEDIUMTEXT NOT NULL, PRIMARY KEY(\"id\"));";
            _cmd.ExecuteNonQuery();
        }
