            addItems(2055, "Badge de milicien Bill Iédebar", "Badge du milicien Bill Iédebar.", 1, 1);
            addItems(2056, "Oeil de Corbac", "Un oeil de corbac est toujours utile, mais attention à ce que le mauvais oeil ne tombe pas sur vous.", 20, 100);
            addItems(2058, "Coeur de Brandon", "Ce coeur humain vient sûrement du célèbre Lhi, qui était passionné par les corbacs.", 20, 500);
