            addItems(6884, "Clef du Donjon du Bworker", "Cette clef permet de rentrer dans le donjon du Bworker. Attention, elle sera détruite après utilisation.", 30, 100);
            addItems(6885, "Jeton du Bworker", "Idéal pour jouer à pile ou face ou pour tuer sur place votre ennemi lors d'un lancer de pièce, ce jeton en or massif fera de vous un joueur heureux, mais peut-être malchanceux.", 100, 100);
            addItems(6886, "Cape des Mercenaires d'Astrub", "Cape des Mercenaires d'Astrub.", 1, 1);
