        void LoadItems() {
            SQLiteCommand _cmd = conn.CreateCommand();
            _cmd.CommandText = "SELECT * FROM Items";
            SQLiteDataReader reader = _cmd.ExecuteReader();

            List<ListViewItem> LVIItems = new List<ListViewItem>();
            List<ListViewItem> LVIHDV = new List<ListViewItem>();
            while (reader.Read()) {
                Item _getItem = new Item() { id = (int)reader["id"], nom = (string)reader["nom"], desc = (string)reader["desc"], lvl = (string)reader["lvl"], prixPnj = (int)reader["prixPnj"], prix1 = (int)reader["prix1"], prix10 = (int)reader["prix10"], prix100 = (int)reader["prix100"] };

                ListViewItem LVI = new ListViewItem(_getItem.id.ToString());
                LVI.SubItems.Add(_getItem.nom);
                LVI.SubItems.Add(_getItem.lvl);
                LVI.SubItems.Add(((int)reader["prixPnj"] / 10).ToString("# ### ### ##0") + " ₭");
                LVI.SubItems.Add(_getItem.desc);
                LVIItems.Add(LVI);

                if (_getItem.prix1 != -1 || _getItem.prix10 != -1 || _getItem.prix100 != -1) {

                    ListViewItem _LVIHDV = new ListViewItem(_getItem.id.ToString());
                    _LVIHDV.SubItems.Add(_getItem.lvl);
                    _LVIHDV.SubItems.Add(_getItem.nom);

                    if (_getItem.prix1 != -1) _LVIHDV.SubItems.Add(((int)reader["prix1"]).ToString("# ### ### ##0") + " ₭"); else _LVIHDV.SubItems.Add("-");

                    if (_getItem.prix10 != -1) {
                        _LVIHDV.SubItems.Add(((int)reader["prix10"]).ToString("# ### ### ##0") + " ₭");
                        _LVIHDV.SubItems.Add(((int)reader["prix10"] / 10).ToString("# ### ### ##0") + " ₭");
                    } else {
                        _LVIHDV.SubItems.Add("-");
                        _LVIHDV.SubItems.Add("-");
                    }
                    if (_getItem.prix100 != -1) {
                        _LVIHDV.SubItems.Add(((int)reader["prix100"]).ToString("# ### ### ##0") + " ₭");
                        _LVIHDV.SubItems.Add(((int)reader["prix100"] / 100).ToString("# ### ### ##0") + " ₭");
                    } else {
                        _LVIHDV.SubItems.Add("-");
                        _LVIHDV.SubItems.Add("-");
                    }

                    _LVIHDV.SubItems.Add(((int)reader["prixPnj"] / 10).ToString("# ### ### ##0") + " ₭");
                    _LVIHDV.SubItems.Add(((int)reader["cout"]).ToString("# ### ### ##0") + " ₭");

                    LVIHDV.Add(_LVIHDV);
                }
            }
            reader.Close();

            if (LVIHDV.Count > 0) {
                lv_hdv.BeginUpdate();
                lv_hdv.Items.AddRange(LVIHDV.ToArray());
                lv_hdv.EndUpdate();
            }

            lv_items.BeginUpdate();
            lv_items.Items.AddRange(LVIItems.ToArray());
            lv_items.EndUpdate();

        }

        public static int getCraft(int id) {
            SQLiteCommand _cmd = conn.CreateCommand();
            _cmd.CommandText = "SELECT * FROM Crafts WHERE \"id\"=" + id;
            SQLiteDataReader reader = _cmd.ExecuteReader();

            int coutCraft = 0;
            string[] _ress;
            if (reader.Read()) _ress = new string[] { (string)reader["ress1"], (string)reader["ress2"], (string)reader["ress3"], (string)reader["ress4"], (string)reader["ress5"], (string)reader["ress6"], (string)reader["ress7"], (string)reader["ress8"] };
            else {
                reader.Close();
                return -1;
            }
            reader.Close();

            foreach (string item in _ress) {
                if (item.Length > 0) {
                    string[] _spltt = item.Split(',');
                    string _qty = _spltt[0];
                    string _id = _spltt[1];
                    Item search = Primaire.getItems(int.Parse(_id));

                    int getPrix = 999999999;
                    if (search.prix1 != -1) getPrix = Math.Min(getPrix, search.prix1);
                    if (search.prix10 != -1) getPrix = Math.Min(getPrix, (int)(search.prix10 / 10));
                    if (search.prix100 != -1) getPrix = Math.Min(getPrix, (int)(search.prix100 / 100));
                    if (search.prix1 == -1 && search.prix10 == -1 && search.prix100 == -1) return -1;

                    if (getPrix != 999999999) coutCraft += (getPrix * int.Parse(_qty));

                } else break;
            }
            return coutCraft;
        }
