    public class ItemComparer : IComparer {
        public int Column { get; set; }
        public bool inverse { get; set; }
        public ItemComparer(int colIndex, bool inv = false) {
            Column = colIndex;
            inverse = inv;
        }
        public int Compare(object a, object b) {
            ListViewItem itemA = a as ListViewItem;
            ListViewItem itemB = b as ListViewItem;

            if (itemA == null && itemB == null)
                return 0;
            else if (itemA == null)
                return -1;
            else if (itemB == null)
                return 1;
            else if (int.Parse(itemA.SubItems[Column].Text.Split('₭')[0].Replace(" ", "").Replace("?","0")) == int.Parse(itemB.SubItems[Column].Text.Split('₭')[0].Replace(" ", "").Replace("?", "0")))
                return 0;
            else if (int.Parse(itemA.SubItems[Column].Text.Split('₭')[0].Replace(" ", "").Replace("?", "0")) > int.Parse(itemB.SubItems[Column].Text.Split('₭')[0].Replace(" ", "").Replace("?", "0")))
                if (!inverse) return -1; else return 1;
            else if (int.Parse(itemA.SubItems[Column].Text.Split('₭')[0].Replace(" ", "").Replace("?", "0")) < int.Parse(itemB.SubItems[Column].Text.Split('₭')[0].Replace(" ", "").Replace("?", "0")))
                if (!inverse) return 1; else return -1;
            else
                return -1;
        }
    }

    public class Perso {
        string id, nom, lvl, classe, couleur1, couleur2, couleur3;

        public Perso(string splt) {
            string[] _splt = splt.Split(';');
            id = _splt[0];
            nom = _splt[1];
            lvl = _splt[2];
            classe = getClasse(_splt[3]);
            couleur1 = _splt[4];
            couleur2 = _splt[5];
            couleur3 = _splt[6];
        }

        public string getClasse(string gfx) {
            string rtn = "";
            switch (gfx.Substring(0, gfx.Length - 1)) {
                case "1":
                    rtn = "Feca";
                    break;
                case "2":
                    rtn = "Osamodas";
                    break;
                case "3":
                    rtn = "Enutrof";
                    break;
                case "4":
                    rtn = "Sram";
                    break;
                case "5":
                    rtn = "Xelor";
                    break;
                case "6":
                    rtn = "Ecaflip";
                    break;
                case "7":
                    rtn = "Eniripsa";
                    break;
                case "8":
                    rtn = "Iop";
                    break;
                case "9":
                    rtn = "Cra";
                    break;
                case "10":
                    rtn = "Sadida";
                    break;
                case "11":
                    rtn = "Sacrieur";
                    break;
                case "12":
                    rtn = "Pandawa";
                    break;
                default:
                    return "Undefined";
            }
            switch (gfx.Substring(1, 1)) {
                case "0":
                    return rtn + " ♂";
                case "1":
                    return rtn + " ♀";
                default:
                    return rtn;
            }
        }

        public string getPerso() {
            return "[" + nom + "] LVL: " + lvl + " classe: " + classe + "\n";
        }
    }

